import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getPosts } from '../services/api';

function BlogList() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await getPosts();
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };
    fetchPosts();
  }, []);

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white shadow-md rounded-lg mt-16">
      <h1 className="text-3xl font-bold mb-6 text-center">Blog Posts</h1>
      {posts.length === 0 ? (
        <p className="text-center text-gray-500">No posts available</p>
      ) : (
        posts.map((post) => (
          <div key={post._id} className="bg-gray-100 p-4 rounded-lg mb-4 shadow-sm">
            <h2 className="text-2xl font-semibold mb-2">
              <Link to={`/post/${post._id}`} className="text-blue-600 hover:underline">
                {post.title}
              </Link>
            </h2>
            <p className="text-gray-700">By <span className="font-medium">{post.author}</span></p>
          </div>
        ))
      )}
    </div>
  );
}

export default BlogList;
