import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getPosts, deletePost } from '../services/api'; // Import deletePost from api

function BlogList() {
  const [posts, setPosts] = useState([]);
  const loggedInUser = localStorage.getItem('username'); // Get the logged-in user's username (assuming it's stored in localStorage)

  // Fetch posts when the component is mounted
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await getPosts();
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };
    fetchPosts();
  }, []);

  // Handle post deletion
  const handleDelete = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this post?');
    if (confirmDelete) {
      try {
        await deletePost(id); // Call deletePost function from API
        setPosts(posts.filter((post) => post._id !== id)); // Update state to remove the deleted post
      } catch (error) {
        console.error('Error deleting post:', error);
      }
    }
  };

  return (
    <div className="max-w-5xl mx-auto p-6 bg-gray-50 shadow-lg rounded-xl mt-16">
      <h1 className="text-4xl font-extrabold mb-8 text-gray-800 text-center">Blog Posts</h1>
      {posts.length === 0 ? (
        <p className="text-center text-gray-500 text-lg">No posts available</p>
      ) : (
        posts.map((post) => (
          <div key={post._id} className="bg-white p-6 rounded-xl mb-6 shadow-md hover:shadow-xl transition-shadow duration-300 ease-in-out">
            <h2 className="text-3xl font-bold mb-3">
              <Link to={`/post/${post._id}`} className="text-blue-700 hover:text-blue-900 hover:underline">
                {post.title}
              </Link>
            </h2>
            <p className="text-gray-600 text-lg">
              By <span className="font-semibold">{post.author}</span>
            </p>
            {/* Add delete/edit button if the post belongs to the logged-in user */}
            {post.author === loggedInUser && (
              <div className="mt-4 flex justify-end space-x-4">
                <Link
                  to={`/edit/${post._id}`}
                  className="bg-yellow-400 text-white px-4 py-2 rounded-md hover:bg-yellow-500 transition-colors"
                >
                  Edit
                </Link>
                <button
                  onClick={() => handleDelete(post._id)}
                  className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition-colors"
                >
                  Delete
                </button>
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
}

export default BlogList;
