import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getPosts } from '../services/api';

function BlogList() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await getPosts();
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };
    fetchPosts();
  }, []);

  return (
    <div className="max-w-5xl mx-auto p-6 bg-gray-50 shadow-lg rounded-xl mt-16">
      <h1 className="text-4xl font-extrabold mb-8 text-gray-800 text-center">Blog Posts</h1>
      {posts.length === 0 ? (
        <p className="text-center text-gray-500 text-lg">No posts available</p>
      ) : (
        posts.map((post) => (
          <div key={post._id} className="bg-white p-6 rounded-xl mb-6 shadow-md hover:shadow-xl transition-shadow duration-300 ease-in-out">
            <h2 className="text-3xl font-bold mb-3">
              <Link to={`/post/${post._id}`} className="text-blue-700 hover:text-blue-900 hover:underline">
                {post.title}
              </Link>
            </h2>
            <p className="text-gray-600 text-lg">By <span className="font-semibold">{post.author}</span></p>
          </div>
        ))
      )}
    </div>
  );
}

export default BlogList;
